import React, { useState } from 'react';
import axios from 'axios';

const serverIP = "localhost";

const Chat = () => {
  const [messages, setMessages] = useState([]);

  const handleUserMessage = async (userMessage) => {
    setMessages((prevMessages) => [
      ...prevMessages,
      { content: userMessage, sender: 'user' },
    ]);

    try {
      const response = await axios.post(`http://${serverIP}:3040/data`, {
        message: userMessage,
      });

      const chatbotResponse = response.data.message;

      setMessages((prevMessages) => [
        ...prevMessages,
        { content: chatbotResponse, sender: 'chatbot' },
      ]);
    } catch (error) {
      console.error('Error:', error);
    }
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    handleUserMessage(event.target.elements.message.value);
    event.target.reset();
  };

  return (
    <div>
      <div className='button'  variant="primary">
        Button as link
      </div>
      <div className='row'>
        <div className='col-2'>
        vedant sethia
        </div>
        <div className='col-10'>
          <div className="chat-window">
            {messages.map((message, index) => (
              <div
                key={index}
                className={`message ${message.sender === 'user' ? 'user' : 'chatbot'} input-field`}
              >
                {message.content}
              </div>
            ))}
          </div>
          <div className='row chat-box'>
            <form className="message-input" onSubmit={handleSubmit}>
              <input
                type="text"
                name="message"
                class="form-control w-75 d-online" aria-label="message…"
                placeholder="Write message…"
              />

              <div 
                type="submit" 
                className='btn btn-primary send-button d-inline'
              >
                <i class="fa fa-paper-plane" aria-hidden="true"></i> Ask NetApp
              </div>
            </form>
          </div>
        </div>
      </div>

    </div>
  );
};

export default Chat;
